import React from 'react';
import logo from './logo.svg';
import './App.css';
import { FaEdit, FaTrashAlt } from 'react-icons/fa';
import { BiAddToQueue } from "react-icons/bi";
import axios from "axios";
import { Switch, Route, Link } from 'react-router-dom';



const baseURL = "localhost:3000/lista";

// reseta o formulário
const initialState = {
  unop:{"nome": "","cnpj": "","situação": "","lista-turnos": [{},{}],
  "lista-restaurantes":[{},{}],"lista-feriados":[{},{}]},
  lista:[]

}

class InserirouEditar extends React.Component {



  constructor(){
    super();
    this.state = {initialState};
  }

  componentWillMount(){
    axios.get(baseURL).then(resp => {
      this.setState({ lista: resp.data });
    })
 }

  clear(){
    this.setState({unop:initialState.unop});
  }

  save(){
    const unop = this.state.unop;
    const method = unop.id?'put':'post';
    const url = unop.id?`${baseURL}/${unop.id}`:baseURL;
    axios[method](url, unop).then(resp => {
      const list = this.getUpdateList(resp.data);
      this.setState({unop:initialState.unop, list});
    })
  }

  getUpdateList(unop, add = true){
    const list = this.state.lista.filter(u=>u.id != unop.id);
    if(add) list.unshift(unop);
    return list 
  }


  updateField(event){
    const unop = this.state.unop;
    unop[event.target.nome] = event.target.value;
    this.setState({unop});
  }





  render(){


    return (
      <div className="App">

        <div id="wrapper">

          <section id="intro" className="main">

            <h2>Formulário de UNOPS</h2>


            <section id="form-inser-edit">


            <form>
              <div className="form-group row">
                <label for="inputEmail3" className="col-sm-2 col-form-label">Código</label>
                <div className="col-sm-10">
                  <input type="number" className="form-control" id="inputEmail3" placeholder="Código" name="id" 
                  value={this.state.unop} onChange={e => {this.updateField(e)}}/>
                </div>
              </div>
              <div className="form-group row">
                <label for="inputPassword3" className="col-sm-2 col-form-label">Nome</label>
                <div className="col-sm-10">
                  <input type="text" className="form-control" id="inputPassword3" placeholder="Nome" name="nome" 
                  value={this.state.unop} onChange={e => {this.updateField(e)}}/>
                </div>
              </div>
              <fieldset className="form-group">
                <div className="row">
                  <legend className="col-form-label col-sm-2 pt-0">Radios</legend>
                  <div className="col-sm-10">
                    <div className="form-check">
                      <input className="form-check-input" type="radio" name="gridRadios" id="gridRadios1" value="option1" checked/>
                      <label className="form-check-label" for="gridRadios1">
                        First radio
                      </label>
                    </div>
                    <div className="form-check">
                      <input className="form-check-input" type="radio" name="gridRadios" id="gridRadios2" value="option2"/>
                      <label className="form-check-label" for="gridRadios2">
                        Second radio
                      </label>
                    </div>
                    <div className="form-check disabled">
                      <input className="form-check-input" type="radio" name="gridRadios" id="gridRadios3" value="option3" disabled/>
                      <label className="form-check-label" for="gridRadios3">
                        Third disabled radio
                      </label>
                    </div>
                  </div>
                </div>
              </fieldset>
              <div className="form-group row">
                <div className="col-sm-2">Checkbox</div>
                <div className="col-sm-10">
                  <div className="form-check">
                    <input className="form-check-input" type="checkbox" id="gridCheck1"/>
                    <label className="form-check-label" for="gridCheck1">
                      Example checkbox
                    </label>
                  </div>
                </div>
              </div>
              <div className="form-group row">
                <div className="col-sm-10">
                  <button type="submit" className="btn btn-primary">Sign in</button>
                </div>
              </div>
            </form>

             </section>


          </section>



        </div>

      </div>
    )






  }



}



export default InserirouEditar;
