import React from 'react';
import logo from './logo.svg';
import './App.css';
import { FaEdit, FaTrashAlt } from 'react-icons/fa';
import { BiAddToQueue } from "react-icons/bi";
import axios from "axios";
import { Switch, Route, Link } from 'react-router-dom';
import InserirouEditar from "./InserirouEditar";



const baseURL = "localhost:3000/lista";

// reseta o formulário
const initialState = {
  unop:{"nome": "","cnpj": "","situação": "","lista-turnos": [{},{}],
  "lista-restaurantes":[{},{}],"lista-feriados":[{},{}]},
  lista:[]

}

class Home extends React.Component {



  constructor(){
    super();
    this.state = {initialState};
  }

  componentWillMount(){
    axios.get(baseURL).then(resp => {
      this.setState({ lista: resp.data });
    })
 }

  clear(){
    this.setState({unop:initialState.unop});
  }

  save(){
    const unop = this.state.unop;
    const method = unop.id?'put':'post';
    const url = unop.id?`${baseURL}/${unop.id}`:baseURL;
    axios[method](url, unop).then(resp => {
      const list = this.getUpdateList(resp.data);
      this.setState({unop:initialState.unop, list});
    })
  }

  getUpdateList(unop, add = true){
    const list = this.state.lista.filter(u=>u.id != unop.id);
    if(add) list.unshift(unop);
    return list 
  }


  updateField(event){
    const unop = this.state.unop;
    unop[event.target.nome] = event.target.value;
    this.setState({unop});
  }





  render(){


    return (
      <div className="App">



          <div class="modal fade" id="modalExemplo" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div class="modal-dialog" role="document">
              <div class="modal-content">
                <div class="modal-header">
                  <h5 class="modal-title" id="exampleModalLabel">Excluir UNOP</h5>

                </div>
                <div class="modal-body">
                Tem certeza que deseja excluir?
                </div>
                <div class="modal-footer">
                  <button type="button" class="btn btn-secondary" data-dismiss="modal">Fechar</button>
                  <button type="button" class="btn btn-primary">Confirmar</button>
                </div>
              </div>
            </div>
          </div>


        <div id="wrapper">

          <section id="intro" className="main">

            <h2>Lista de UNOPS</h2>
            <section id="filtros">
                <form>

                <div class="form-group">
                  <input type="text" class="form-control form-control-lg" id="inputAddress" placeholder="Pesquisar UNOP"/>
                </div>
                <div class="form-group">
                <select class="form-control form-control-lg form-control-select">
                  <option>Situação</option>
                </select>
                </div>
              </form>
            </section>

            <section id="lista-unops">

              <table class="table">
              <thead class="thead-dark">
                <tr>
                  <th scope="col">Lista de UNOPS</th>
                  <th scope="col">Código da UNOP</th>
                  <th scope="col">Situação</th>
                  <th scope="col">Edição</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <th scope="row">Minuano</th>
                  <td>10001</td>
                  <td>Otto</td>
                  <td>
                  <Link to="/insert-edit"><FaEdit /></Link>     
                  <Link to="/insert-edit/"><BiAddToQueue/></Link>    
                 <Link data-toggle="modal" data-target="#modalExemplo"><FaTrashAlt/></Link></td>
                </tr>
                <tr>
                  <th scope="row">2</th>
                  <td>Jacob</td>
                  <td>Thornton</td>
                  <td><i class="far fa-edit"></i></td>
                </tr>
                <tr>
                  <th scope="row">3</th>
                  <td>Larry</td>
                  <td>the Bird</td>
                  <td><i class="far fa-edit"></i></td>
                </tr>
              </tbody>
            </table>

            <nav aria-label="Navegação de página exemplo">
  <ul class="pagination">
    <li class="page-item">
      <a class="page-link page-link-dark-back" href="#" aria-label="Anterior">
        <span aria-hidden="true">&lsaquo;</span>
        <span class="sr-only">Anterior</span>
      </a>
    </li>
    <li class="page-item"><a class="page-link" href="#">1</a></li>
    <li class="page-item"><a class="page-link" href="#">2</a></li>
    <li class="page-item"><a class="page-link" href="#">3</a></li>
    <li class="page-item">
      <a class="page-link page-link-dark-back" href="#" aria-label="Próximo">
        <span aria-hidden="true">&rsaquo;</span>
        <span class="sr-only">Próximo</span>
      </a>
    </li>
  </ul>
</nav>
          </section>



          </section>



        </div>





      </div>
    )






  }



}



export default Home;
