import React from 'react';
import logo from './logo.svg';
import './App.css';
import { FaEdit, FaTrashAlt, FaSearch } from 'react-icons/fa';
import { BiAddToQueue } from "react-icons/bi";
import axios from "axios";
import { Switch, Route, Link } from 'react-router-dom';
import InserirouEditar from "./InserirouEditar";
import PostData from "./data/db.json"



// const baseURL = " http://localhost:3001/unop";
const baseURL = [{
  "id": 10001,
  "nome": "Minuano",
  "cnpj": "36424694000131",
  "situação": "Ativa",
  "lista-turnos": [
      {
       "id": 1,
       "nome":"Manhã"
      },
      {
       "id": 2,
       "nome":"Tarde"
      },
      {
      "id": 2,
      "nome":"Noite"
      }
  ]
  ,
  "lista-restaurantes": [
         {
          "id": 1,
          "nome":"Restaurante 1"
         },
         {
          "id": 2,
          "nome":"Restaurante 2"
         },
         {
         "id": 2,
         "nome":"Restaurante 3"
         }
  ],
  "lista-feriados": [
         {
          "id": 1,
          "nome":"13 de Agosto de 2019"
         },
         {
          "id": 2,
          "nome":"15 de Novembro de 2019"
         },
         {
         "id": 2,
         "nome":"24 de Dezembro de 2019"
         }
  ]
}];

// reseta o formulário
const initialState = {
  unop:{"nome": "","cnpj": "","situação": "","lista-turnos": [],
  "lista-restaurantes":[],"lista-feriados":[]},
  list:[]

}

class Home extends React.Component {

  state ={initialState};



  componentWillMount(){
    axios.get(baseURL).then(resp => {
      this.setState({ list: resp.data });
    })
 }

  clear(){
    this.setState({unop:initialState.unop});
  }

  save(){
    const unop = this.state.unop;
    const method = unop.id?'put':'post';
    const url = unop.id?`${baseURL}/${unop.id}`:baseURL;
    axios[method](url, unop).then(resp => {
      const list = this.getUpdateList(resp.data);
      this.setState({unop:initialState.unop, list});
    })
  }

  getUpdateList(unop, add = true){
    const list = this.state.list.filter(u=>u.id != unop.id);
    if(add) list.unshift(unop);
    return list 
  }


  updateField(event){
    const unop = this.state.unop;
    unop[event.target.nome] = event.target.value;
    this.setState({unop});
  }


  load(unop){
     this.setState({ unop });
  }

  remove(unop){
    axios.delete(`${baseURL}/${unop.id}`).then(resp => {
      const list = this.getUpdateList(unop, false);
      this.setState({ list });
    });
  }

  // tabela
  renderTable(){

    return (
      <table className="table">
      <thead className="thead-dark">
        <tr>
          <th scope="col">Lista de UNOPS</th>
          <th scope="col">Código da UNOP</th>
          <th scope="col">Situação</th>
          <th scope="col">Edição</th>
        </tr>
      </thead>
      <tbody>
       {this.renderRows()}
      </tbody>
    </table>

    );

    }


    renderRows(){

      return PostData.unops.map((PostDetail, index) => {
        return (
          <tr key={PostDetail.id}>
            <th scope="col">{PostDetail.nome}</th>
            <td>{PostDetail.id}</td>
            <td>{PostDetail.situação}</td>
            <td>
              <Link to="/insert-edit"  className="talbe-links-home"><FaEdit /></Link>     
            </td>
          </tr>
        );
      });



    }

    // campos da tabela

    // renderRows(){

    //  alert(PostData.unops.length);



    // }

  render(){


    return (
      <div className="App">





        <div id="wrapper">

          <section id="intro" className="main">

            <h2>Lista de UNOPS</h2>
            <section id="filtros">
                <form class="nav-form">

                <div className="input-group input-group-search-top">
                  <input type="search" className="form-control form-control-lg" id="inputAddress" placeholder="Pesquisar UNOP"/>
                  <div className="input-group-btn ">
                  <button type="submit" class="btn btn-default">
                    <FaSearch/>
                  </button>
                  </div>
                </div>

                <div className="form-group form-control-select">
                      <select className="form-control form-control-lg">
                        <option>Situação</option>
                      </select>
                </div>
              </form>
            </section>

            <section id="lista-unops">




             {/* Tabela */}

             {this.renderTable()}


             {/* Páginação */}
             <nav aria-label="Navegação de página exemplo">
                <ul className="pagination">
                  <li className="page-item">
                    <a className="page-link page-link-dark-back" href="#" aria-label="Anterior">
                      <span aria-hidden="true">&lsaquo;</span>
                      <span className="sr-only">Anterior</span>
                    </a>
                  </li>
                  <li className="page-item"><a className="page-link" href="#">1</a></li>
                  <li className="page-item"><a className="page-link" href="#">2</a></li>
                  <li className="page-item"><a className="page-link" href="#">3</a></li>
                  <li className="page-item">
                    <a className="page-link page-link-dark-back" href="#" aria-label="Próximo">
                      <span aria-hidden="true">&rsaquo;</span>
                      <span className="sr-only">Próximo</span>
                    </a>
                  </li>
                </ul>
              </nav>
          </section>



          </section>



        </div>





      </div>
    )






  }



}



export default Home;
