                        <tr>
                          <th scope="col">Turnos Selecionados</th>
                          <th scope="col"></th>
                        </tr>
                      </thead>
                      <tbody> 
                          { this.renderRowsTurnos()}  
                          <tr>
                            <td></td>
                              <td><a href="/inserir" className="btn btn-dark">Incluir Turno</a></td>
                          </tr>  
                          </tbody>
                     </table>                         


                );
              // }




      }

}


renderRowsTurnos(){


  if(initialState.list.length > 0){

    objLocal=initialState.list.filter(u=>u.id == urlParams.get("value"));

    console.log(objLocal);

    return objLocal[0]["lista-turnos"].map(obj => {
      return(

         <tr key={obj.id}>
          <th scope="row">{obj.nome}</th>
          <td><Link className="talbe-links" data-toggle="modal" data-target="#exampleModal"><FaTrashAlt /></Link>     
          </td>
        </tr>



      );

       })

      }
  }
